package demo.micro.service.app;

import demo.micro.service.list.LinkedList;

import static demo.micro.service.utilities.StringUtils.join;
import static demo.micro.service.utilities.StringUtils.split;
import static demo.micro.service.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
